import React, { useEffect, useState } from "react";
import axios from "axios";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Typography from "@material-ui/core/Typography";
import { SYMBOLS, SHORT_DATE_FORMAT } from "../../Constants/Constants";
import useStyles from "../styles";
import Grid from "@material-ui/core/Grid";
import format from "date-fns/format";

const Next3Days = () => {
  const classes = useStyles();
  const [forecast, setForecast] = useState(null);
  const [selectedDate, handleDateChange] = useState(new Date());
  const [location, setLocation] = useState("belfast");
  const [days] = useState(3);
  const [isForecast, setIsForecast] = useState(true);
  const [dailyForecast, setDailyForecast] = useState({});

  useEffect(() => {
    const getForecast = () => {
      axios
        .get(`${process.env.REACT_APP_FORECAST_API}=${location}&days=${days}`)
        .then((data) => {
          setForecast(data.data);
          setDailyForecast(data.data.forecast);
          setIsForecast(true);
        })
        .catch((error) => console.log(error));
    };
    getForecast();
  }, []);

  return (
    <Grid container spacing="2">
      {forecast && isForecast === true && (
        <Grid>
          <Typography variant="h2">
            Next 3 Days for {forecast.location.name}
          </Typography>

          {dailyForecast.forecastday.length > 0
            ? dailyForecast.forecastday.map((forecastday) => {
                return (
                  <>
                    <Grid xs={12}>
                      {format(new Date(forecastday.date), SHORT_DATE_FORMAT)}
                    </Grid>
                    <Card className={classes.root}>
                      <CardContent>
                        <Typography className={classes.text}>
                          {forecastday.day.avgtemp_c}
                          {SYMBOLS.DEGREES} {""}
                          {forecastday.day.avgtemp_f}
                          {SYMBOLS.FAHRENHEIT}
                        </Typography>
                        <Typography className={classes.text}>
                          Sunrise: {forecastday.astro.sunrise}
                        </Typography>
                        <Typography className={classes.text}>
                          Sunset: {forecastday.astro.sunset}
                        </Typography>

                        <Typography className={classes.text}>
                          {forecastday.day.condition.text}
                        </Typography>
                      </CardContent>
                      <CardMedia
                        className={classes.media}
                        image={forecastday.day.condition.icon}
                      />
                    </Card>
                  </>
                );
              })
            : null}
        </Grid>
      )}
    </Grid>
  );
};

export default Next3Days;
