import React, { useState } from "react";

import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import { SYMBOLS, SHORT_DATE_FORMAT } from "../../Constants/Constants";
import useStyles from "../styles";
import Grid from "@material-ui/core/Grid";
import format from "date-fns/format";
import { KeyboardDatePicker } from "@material-ui/pickers";
import Box from "@material-ui/core/Box";
const History = ({ histroyDay }) => {
  const classes = useStyles();
  const [selectedDate, handleDateChange] = useState(new Date());

  return (
    <>
      <Typography variant="h2" className={classes.title}>
        View History
      </Typography>
      <Grid item xs={12}>
        <Grid item xs={12}>
          <KeyboardDatePicker
            label="Select Date"
            clearable
            maxDate={new Date()}
            format="yyyy/MM/dd"
            value={selectedDate}
            onChange={(date) => handleDateChange(date)}
          />
          {format(new Date(histroyDay.date), SHORT_DATE_FORMAT)}
        </Grid>

        <Card>
          <CardContent>
            <Box mb={2} ml={4} mr={-4}>
              <Grid container spacing={2} justify="space-between">
                <Grid item xs={4}>
                  <Typography className={classes.subTitle}>
                    {histroyDay.day.mintemp_c} {SYMBOLS.DEGREES}
                  </Typography>
                  <Typography className={classes.subText}>Low</Typography>
                </Grid>
                <Grid item xs={4}>
                  <Typography className={classes.subTitle}>
                    {histroyDay.day.maxwind_mph} mph
                  </Typography>
                  <Typography className={classes.subText}>Wind</Typography>
                </Grid>
                <Grid item xs={4}>
                  <Typography className={classes.subTitle}>
                    {histroyDay.astro.sunrise}
                  </Typography>
                  <Typography className={classes.subText}>Sunrise</Typography>
                </Grid>
              </Grid>
            </Box>

            <Box ml={4} mr={-4}>
              <Grid container spacing={2} justify="space-between">
                <Grid item xs={4}>
                  <Typography className={classes.subTitle}>
                    {histroyDay.day.maxtemp_c} {SYMBOLS.DEGREES}
                  </Typography>
                  <Typography className={classes.subText}>High</Typography>
                </Grid>
                <Grid item xs={4}>
                  <Typography className={classes.subTitle}>
                    {histroyDay.day.daily_will_it_rain === 1 ? "Yes" : "No"}
                  </Typography>
                  <Typography className={classes.subText}>Rain</Typography>
                </Grid>
                <Grid item xs={4}>
                  <Typography className={classes.subTitle}>
                    {histroyDay.astro.sunset}
                  </Typography>
                  <Typography className={classes.subText}>Sunset</Typography>
                </Grid>
              </Grid>
            </Box>
          </CardContent>
        </Card>
      </Grid>
    </>
  );
};

export default History;
