import React, { useEffect, useState, useCallback } from "react";
import axios from "axios";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import {
  SYMBOLS,
  LONG_DATE_FORMAT,
  LONG_DATE_TIME_FORMAT,
  SHORT_DATE_FORMAT,
  VALID_DATE_FORMAT,
} from "../../Constants/Constants";
import useStyles from "./styles";
import Grid from "@material-ui/core/Grid";
import { useParams } from "react-router-dom";
import Box from "@material-ui/core/Box";
import format from "date-fns/format";
import HourlyWeather from "../../Components/HourlyWeather/HourlyWeather";
import Tab from "@material-ui/core/Tab";
import Tabs from "@material-ui/core/Tabs";
import { KeyboardDatePicker } from "@material-ui/pickers";
import CardMedia from "@material-ui/core/CardMedia";

const SearchDetails = () => {
  const classes = useStyles();
  const [weather, setWeather] = useState(null);
  const { location: urlGroupId } = useParams();
  const [location, setLocation] = useState("");
  const [isToday, setIsToday] = useState(true);
  const [days] = useState(3);
  const [isForecast, setIsForecast] = useState(false);
  const [dailyForecast, setDailyForecast] = useState({});
  const [forecast, setForecast] = useState(null);
  const [activeTab, setActiveTab] = useState(0);
  const [validDate, setValidDate] = useState("");
  const [isHistory, setIsHistory] = useState(false);
  const [selectedDate, handleDateChange] = useState(new Date());
  const [history, setHistory] = useState(null);
  const [historyForecast, setHistoryForecast] = useState({});
  const [date, setDate] = useState(
    format(new Date(selectedDate), VALID_DATE_FORMAT)
  );

  useEffect(() => {
    if (urlGroupId) {
      setLocation(urlGroupId);
    }
  }, [urlGroupId]);

  useEffect(() => {
    if (location !== "") {
      getToday();
      getForecast();
      dateToday();
    }
  }, [location]);

  const dateToday = () => {
    const date = format(new Date(), VALID_DATE_FORMAT);
    setValidDate(date);
  };

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  const getHistory = useCallback(async () => {
    const newDate = new Date(selectedDate).toISOString().split("T")[0];
    setDate(newDate);
    axios
      .get(`${process.env.REACT_APP_HISTORY_API}=${location}&dt=${date}`)
      .then((data) => {
        setHistory(data.data.forecast);
        setHistoryForecast(data.data.forecast);
        console.log(data.data.forecast);
        setIsHistory(true);
      })
      .catch((error) => console.log(error));
  }, [location, selectedDate, date]);

  useEffect(() => {
    getHistory();
  }, [getHistory]);

  const getToday = useCallback(async () => {
    axios
      .get(`${process.env.REACT_APP_WEATHER_API}=${location}`)
      .then((data) => {
        setWeather(data.data);
        setIsToday(true);
      })
      .catch((error) => console.log(error));
  }, [location]);

  const getForecast = useCallback(async () => {
    axios
      .get(`${process.env.REACT_APP_FORECAST_API}=${location}&days=${days}`)
      .then((data) => {
        setForecast(data.data);
        setDailyForecast(data.data.forecast);
        setIsForecast(true);
      })

      .catch((error) => console.log(error));
  }, [days, location]);

  const getMinDate = () => {
    const date = new Date();
    date.setDate(date.getDate() - 8);
  };

  return (
    <Box ml={4}>
      <Grid container spacing={2}>
        {weather && isToday === true && (
          <>
            <Grid item xs={12}>
              <Box mt={2}>
                <Typography variant="h2" className={classes.title}>
                  Today in {weather.location.name}, {""}{" "}
                  {weather.location.country}
                </Typography>
              </Box>
            </Grid>
            <Grid item xs={12}>
              <Box mb={2} mt={2}>
                <Typography variant="h5" className={classes.subTitle}>
                  {format(
                    new Date(weather.location.localtime),
                    LONG_DATE_FORMAT
                  )}
                </Typography>
              </Box>
            </Grid>

            <Grid container spacing={2} direction="row" justify="space-between">
              <Grid item md={1} xs={12}>
                <Box>
                  <img
                    className={classes.media}
                    src={weather.current.condition.icon}
                    alt="weather"
                  ></img>
                </Box>
              </Grid>
              <Grid item md={2} xs={12}>
                <Typography className={classes.degrees}>
                  {weather.current.temp_c} {SYMBOLS.DEGREES}
                </Typography>
                <Typography className={classes.degrees}>
                  {weather.current.condition.text}
                </Typography>
              </Grid>
              <Grid item md={6} xs={12}>
                <Card
                  className={
                    weather.current.is_day === 1 ? classes.night : classes.root
                  }
                >
                  <CardContent>
                    {forecast &&
                      isForecast === true &&
                      dailyForecast.forecastday.map((forecastday, index) => {
                        return (
                          <>
                            {validDate === forecastday.date ? (
                              <Box mb={2} ml={4} mr={-4}>
                                <Grid
                                  container
                                  spacing={2}
                                  justify="space-between"
                                >
                                  <Grid item xs={4}>
                                    <Typography className={classes.subTitle}>
                                      {forecastday.day.mintemp_c}{" "}
                                      {SYMBOLS.DEGREES}
                                    </Typography>
                                    <Typography className={classes.subText}>
                                      Low
                                    </Typography>
                                  </Grid>
                                  <Grid item xs={4}>
                                    <Typography className={classes.subTitle}>
                                      {forecastday.day.maxwind_mph} mph
                                    </Typography>
                                    <Typography className={classes.subText}>
                                      Wind
                                    </Typography>
                                  </Grid>
                                  <Grid item xs={4}>
                                    <Typography className={classes.subTitle}>
                                      {forecastday.astro.sunrise}
                                    </Typography>
                                    <Typography className={classes.subText}>
                                      Sunrise
                                    </Typography>
                                  </Grid>
                                </Grid>
                              </Box>
                            ) : null}
                            {validDate === forecastday.date ? (
                              <Box ml={4} mr={-4}>
                                <Grid
                                  container
                                  spacing={2}
                                  justify="space-between"
                                >
                                  <Grid item xs={4}>
                                    <Typography className={classes.subTitle}>
                                      {forecastday.day.maxtemp_c}{" "}
                                      {SYMBOLS.DEGREES}
                                    </Typography>
                                    <Typography className={classes.subText}>
                                      High
                                    </Typography>
                                  </Grid>
                                  <Grid item xs={4}>
                                    <Typography className={classes.subTitle}>
                                      {forecastday.day.daily_will_it_rain === 1
                                        ? "Yes"
                                        : "No"}
                                    </Typography>
                                    <Typography className={classes.subText}>
                                      Rain
                                    </Typography>
                                  </Grid>
                                  <Grid item xs={4}>
                                    <Typography className={classes.subTitle}>
                                      {forecastday.astro.sunset}
                                    </Typography>
                                    <Typography className={classes.subText}>
                                      Sunset
                                    </Typography>
                                  </Grid>
                                </Grid>
                              </Box>
                            ) : null}
                          </>
                        );
                      })}
                  </CardContent>
                </Card>
              </Grid>
            </Grid>
            <Grid container spacing={2} direction="row" justify="space-between">
              <Grid item xs={12}>
                <Typography variant="h2" className={classes.title}>
                  Forecast
                </Typography>
              </Grid>

              {/* FORECAST  */}
              <Grid>
                <Typography variant="h2"></Typography>
                {isForecast === true ? (
                  <Grid item xs={12}>
                    <Tabs
                      value={activeTab}
                      indicatorColor="primary"
                      textColor="primary"
                      onChange={handleTabChange}
                      aria-label="Weather Tab"
                      variant="scrollable"
                    >
                      {dailyForecast.forecastday.map((forecastday, index) => {
                        return (
                          <Tab
                            key={`${forecastday.date}_tab_${index}`}
                            label={format(
                              new Date(forecastday.date),
                              SHORT_DATE_FORMAT
                            )}
                          />
                        );
                      })}
                    </Tabs>
                  </Grid>
                ) : (
                  <Typography>No forecast Available</Typography>
                )}
              </Grid>
              <Grid container spacing="2">
                <Grid item xs={12}>
                  {forecast &&
                    isForecast === true &&
                    dailyForecast.forecastday.map((forecastday, index) => {
                      return (
                        <React.Fragment
                          key={`display_weather_details_${forecastday.date}`}
                        >
                          {activeTab === index && (
                            <HourlyWeather
                              tabNumber={index}
                              forecastday={forecastday}
                            />
                          )}
                        </React.Fragment>
                      );
                    })}
                </Grid>
              </Grid>
              <Grid
                container
                spacing="2"
                direction="row"
                justify="space-between"
              >
                {history && (
                  <Grid>
                    {isHistory === true ? (
                      historyForecast.forecastday.map((forecastday, index) => {
                        return (
                          <>
                            <Typography variant="h2" className={classes.title}>
                              View History
                            </Typography>
                            <Grid item xs={12}>
                              <Grid item xs={12}>
                                <KeyboardDatePicker
                                  label="Select Date"
                                  clearable
                                  minDate={getMinDate}
                                  maxDate={new Date()}
                                  format="yyyy/MM/dd"
                                  value={selectedDate}
                                  onChange={(date) => handleDateChange(date)}
                                />
                              </Grid>

                              <Card className={classes.root}>
                                <CardContent>
                                  <Typography>
                                    {" "}
                                    {format(
                                      new Date(forecastday.date),
                                      VALID_DATE_FORMAT
                                    )}
                                  </Typography>
                                  <Typography className={classes.text}>
                                    {forecastday.day.avgtemp_c}
                                    {SYMBOLS.DEGREES} {""}
                                    {forecastday.day.avgtemp_f}
                                    {SYMBOLS.FAHRENHEIT}
                                  </Typography>
                                  <Typography className={classes.text}>
                                    Sunrise: {forecastday.astro.sunrise}
                                  </Typography>
                                  <Typography className={classes.text}>
                                    Sunset: {forecastday.astro.sunset}
                                  </Typography>

                                  <Typography className={classes.text}>
                                    {forecastday.day.condition.text}
                                  </Typography>
                                </CardContent>
                                <CardMedia
                                  className={classes.media}
                                  image={forecastday.day.condition.icon}
                                />
                              </Card>
                            </Grid>
                          </>
                        );
                      })
                    ) : (
                      <Typography>No forecast found</Typography>
                    )}
                  </Grid>
                )}
              </Grid>

              <Box mt={4}>
                <Grid item xs={12}>
                  <Typography variant="h5" className={classes.subTitle}>
                    Last Updated: {""}
                    {format(
                      new Date(weather.current.last_updated),
                      LONG_DATE_TIME_FORMAT
                    )}
                  </Typography>
                </Grid>
              </Box>
            </Grid>
          </>
        )}
      </Grid>
    </Box>
  );
};
export default SearchDetails;
