import React, { useState } from "react";
import axios from "axios";
import Grid from "@material-ui/core/Grid";
import { Typography } from "@material-ui/core";
import Card from "@material-ui/core/Card";
import Link from "@material-ui/core/Link";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import { Link as RouterLink } from "react-router-dom";
const Search = () => {
  const [input, setInput] = useState("");
  const [searchData, setSearchData] = useState({});
  const [data, setData] = useState(false);
  //Search event
  const weatherInput = (e) => {
    setInput(e.target.value);
  };

  const searchWeather = () => {
    axios.get(`${process.env.REACT_APP_SEARCH_API}=${input}`).then((data) => {
      setSearchData(data.data);
      setData(true);
    });
  };

  return (
    <Grid container spacing="2">
      <Grid item xs={6}>
        <input
          onChange={weatherInput}
          type="text"
          placeholder="Type in a city, country or postcode"
        />
        <button onClick={searchWeather}>Search</button>
        <button type="reset" value="Reset">
          Clear
        </button>
      </Grid>
      {data === true &&
        searchData.map((location) => {
          return (
            <>
              <Grid container>
                <Grid item xs={4} spacing={2}>
                  <Card>
                    <CardContent id={location.id}>
                      <Link
                        to={`/search/${location.name.split(",")[0]}`}
                        component={RouterLink}
                        underline="none"
                      >
                        <Typography gutterBottom variant="h4">
                          {location.name.split(",")[0]}
                        </Typography>
                      </Link>
                      <Typography gutterBottom variant="h5">
                        {location.country}
                      </Typography>
                    </CardContent>
                    <CardMedia />
                  </Card>
                </Grid>
              </Grid>
            </>
          );
        })}
    </Grid>
  );
};

export default Search;
