import React, { useEffect, useState } from "react";
import axios from "axios";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Typography from "@material-ui/core/Typography";
import {
  SYMBOLS,
  SHORT_DATE_FORMAT,
  VALID_DATE_FORMAT,
} from "../../Constants/Constants";
import useStyles from "./styles";
import Geocode from "react-geocode";
import Grid from "@material-ui/core/Grid";
import format from "date-fns/format";
import Tab from "@material-ui/core/Tab";
import Tabs from "@material-ui/core/Tabs";
import { KeyboardDatePicker } from "@material-ui/pickers";
import { Link } from "react-router-dom";

const Weather = () => {
  Geocode.setApiKey(`${process.env.REACT_APP_GOOGLE_MAPS_API}`);
  const classes = useStyles();
  const [input, setInput] = useState("");
  const [weather, setWeather] = useState(null);
  const [activeTab, setActiveTab] = useState(0);
  const [forecast, setForecast] = useState(null);
  const [selectedDate, handleDateChange] = useState(new Date());
  // const [latitude, setLatitude] = useState("");
  // const [longitude, setLongitude] = useState("");
  const [location, setLocation] = useState("belfast");
  const [days] = useState(3);
  const [isToday, setIsToday] = useState(true);
  const [isForecast, setIsForecast] = useState(false);
  const [dailyForecast, setDailyForecast] = useState({});
  const [isHistory, setIsHistory] = useState(false);
  const [history, setHistory] = useState(null);
  const [historyForecast, setHistoryForecast] = useState({});
  const [date, setDate] = useState(
    format(new Date(selectedDate), VALID_DATE_FORMAT)
  );

  //Search event
  const weatherInput = (e) => {
    setInput(e.target.value);
  };

  const searchWeather = () => {
    if (isToday === true) {
      axios
        .get(`${process.env.REACT_APP_WEATHER_API}=${input}`)
        .then((data) => {
          console.log(data.data);
          setWeather(data.data);
        });
    }
    if (isForecast === true) {
      axios
        .get(`${process.env.REACT_APP_FORECAST_API}=${input}`)
        .then((data) => {
          console.log(data.data.location.name);
          setForecast(data.data);
          setLocation(data.data.location.name);
          getForecast();
        });
    }
  };

  const getForecast = () => {
    setIsToday(false);
    setIsForecast(false);
    axios
      .get(`${process.env.REACT_APP_FORECAST_API}=${location}&days=${days}`)
      .then((data) => {
        setForecast(data.data);
        setDailyForecast(data.data.forecast);
        setIsForecast(true);
      })
      .catch((error) => console.log(error));
  };

  const getHistory = () => {
    setIsToday(false);
    setIsForecast(false);
    const newDate = new Date(selectedDate).toISOString().split("T")[0];
    setDate(newDate);
    axios
      .get(`${process.env.REACT_APP_HISTORY_API}=${location}&dt=${date}`)
      .then((data) => {
        setHistory(data.data);
        setHistoryForecast(data.data.forecast);
        setIsHistory(true);
      })
      .catch((error) => console.log(error));
  };

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  return (
    <>
      <Grid container spacing="2">
        <Grid item xs={6} className={classes.row}></Grid>
        <Grid item xs={6}>
          <input onChange={weatherInput} type="text" />
          <button onClick={searchWeather}>Search</button>
        </Grid>
        <Tabs
          value={activeTab}
          indicatorColor="primary"
          textColor="primary"
          onChange={handleTabChange}
          aria-label="Weather Tab"
          variant="scrollable"
        >
          <Tab label={"Today"} value={0} id="Tab0" />
          <Tab
            label={"Next 3 Days"}
            value={1}
            id="Tab1"
            onClick={getForecast}
          />
          <Tab label={"History"} value={2} id="Tab2" onClick={getHistory} />
        </Tabs>

        {weather && isToday === true && (
          <Grid xs={12}>
            <Typography variant="h2">Today</Typography>
            <Card className={classes.root}>
              <CardContent>
                <Typography gutterBottom variant="h4" className={classes.text}>
                  {weather.location.name}
                </Typography>
                <Typography variant="h5" className={classes.text}>
                  {weather.location.country}
                </Typography>
                <Typography className={classes.text}>
                  {weather.current.temp_c} {SYMBOLS.DEGREES} {""}
                  {weather.current.temp_f} {SYMBOLS.FAHRENHEIT}
                </Typography>
                <Typography className={classes.text}>
                  {weather.current.condition.text}
                </Typography>
              </CardContent>
              <CardMedia
                className={classes.media}
                image={weather.current.condition.icon}
              />
            </Card>
          </Grid>
        )}
      </Grid>
      {forecast && isForecast === true ? (
        <Grid container spacing="2">
          <Typography variant="h2">
            Next 3 Days for {forecast.location.name}
          </Typography>
          {dailyForecast.forecastday.length > 0 ? (
            dailyForecast.forecastday.map((forecastday) => {
              return (
                <Grid xs={12}>
                  {format(new Date(forecastday.date), SHORT_DATE_FORMAT)}
                  <Link to={`/${forecastday.date}`} underline="none">
                    View More
                  </Link>
                  <Card className={classes.root}>
                    <CardContent>
                      <Typography className={classes.text}>
                        {forecastday.day.avgtemp_c}
                        {SYMBOLS.DEGREES} {""}
                        {forecastday.day.avgtemp_f}
                        {SYMBOLS.FAHRENHEIT}
                      </Typography>
                      <Typography className={classes.text}>
                        Sunrise: {forecastday.astro.sunrise}
                      </Typography>
                      <Typography className={classes.text}>
                        Sunset: {forecastday.astro.sunset}
                      </Typography>

                      <Typography className={classes.text}>
                        {forecastday.day.condition.text}
                      </Typography>
                    </CardContent>
                    <CardMedia
                      className={classes.media}
                      image={forecastday.day.condition.icon}
                    />
                  </Card>
                </Grid>
              );
            })
          ) : (
            <Typography>No forecast found</Typography>
          )}
        </Grid>
      ) : (
        history &&
        isHistory === true && (
          <Grid container spacing="2">
            <Typography variant="h2">{history.location.name}</Typography>
            {historyForecast.forecastday.length > 0 ? (
              historyForecast.forecastday.map((forecastday) => {
                return (
                  <Grid xs={12}>
                    <Grid xs={12}>
                      <KeyboardDatePicker
                        label="Select Date"
                        clearable
                        maxDate={new Date()}
                        format="yyyy/MM/dd"
                        value={selectedDate}
                        onChange={(date) => handleDateChange(date)}
                      />
                    </Grid>
                    {format(new Date(forecastday.date), VALID_DATE_FORMAT)}
                    <Card className={classes.root}>
                      <CardContent>
                        <Typography className={classes.text}>
                          {forecastday.day.avgtemp_c}
                          {SYMBOLS.DEGREES} {""}
                          {forecastday.day.avgtemp_f}
                          {SYMBOLS.FAHRENHEIT}
                        </Typography>
                        <Typography className={classes.text}>
                          Sunrise: {forecastday.astro.sunrise}
                        </Typography>
                        <Typography className={classes.text}>
                          Sunset: {forecastday.astro.sunset}
                        </Typography>

                        <Typography className={classes.text}>
                          {forecastday.day.condition.text}
                        </Typography>
                      </CardContent>
                      <CardMedia
                        className={classes.media}
                        image={forecastday.day.condition.icon}
                      />
                    </Card>
                  </Grid>
                );
              })
            ) : (
              <Typography>No forecast found</Typography>
            )}
          </Grid>
        )
      )}
    </>
  );
};

export default Weather;
