import { makeStyles } from "@material-ui/core/styles";

export default makeStyles(() => {
  return {
    root: {
      minWidth: 275,
      maxWidth: 500,
      // textAlign: "center",
      background: "#012c40",
      display: "flex",
      color: "#FFFFFF",
    },
    night: {
      minWidth: 275,
      maxWidth: 500,
      textAlign: "center",
      background: "#00bfff",
      display: "flex",
      // opacity: "0.5",
    },
    text: {
      color: "#FFFFFF",
      fontWeight: "600px",
      fontSize: "25px",
      justifyContent: "center",
      alignItems: "center",
    },
    media: {
      height: "250px",
      width: "250px",
    },
    title: {
      color: "#FFFFFF",
      fontWeight: "600px",
      display: "flex",
      fontSize: "50px",
    },
    degrees: {
      color: "#FFFFFF",
      fontWeight: "600px",
      fontSize: "50px",
      display: "flex",
    },
    subTitle: {
      color: "#FFFFFF",
      fontWeight: "600px",
      display: "flex",
      fontSize: "20px",
      justifyContent: "flex-start",
    },
    subText: {
      color: "#FFFFFF",
      fontWeight: "600px",
      display: "flex",
      fontSize: "15px",
      justifyContent: "flex-start",
    },
    row: {
      display: "flex",
      flexDirection: "row",
      justifyContent: "flex-start",
      marginTop: "6px",
      marginBottom: "15px",
    },
    margin: {
      marginBottom: "20px",
      marginTop: "20px",
    },
    cardText: {
      textAlign: "right",
    },
  };
});
