import React, { useEffect, useState, useCallback } from "react";
import axios from "axios";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import { SYMBOLS, LONG_DATE_FORMAT } from "../../Constants/Constants";
import useStyles from "./styles";
import Grid from "@material-ui/core/Grid";
import { useParams } from "react-router-dom";
import Box from "@material-ui/core/Box";
import format from "date-fns/format";

const SearchDetails = () => {
  const classes = useStyles();
  const [weather, setWeather] = useState(null);
  const { location: urlGroupId } = useParams();
  const [location, setLocation] = useState("");
  const [isToday, setIsToday] = useState(true);
  const [days] = useState(1);
  const [isForecast, setIsForecast] = useState(false);
  const [dailyForecast, setDailyForecast] = useState({});
  const [forecast, setForecast] = useState(null);

  useEffect(() => {
    if (urlGroupId) {
      setLocation(urlGroupId);
    }
  }, [urlGroupId]);

  useEffect(() => {
    if (location !== "") {
      getToday();
      getForecast();
    }
  }, [location]);

  const getToday = () => {
    axios
      .get(`${process.env.REACT_APP_WEATHER_API}=${location}`)
      .then((data) => {
        setWeather(data.data);
        setIsToday(true);
      })
      .catch((error) => console.log(error));
  };

  const getForecast = useCallback(async () => {
    axios
      .get(`${process.env.REACT_APP_FORECAST_API}=${location}&days=${days}`)
      .then((data) => {
        setForecast(data.data);
        setDailyForecast(data.data.forecast);
        console.log(data.data.forecast);
        setIsForecast(true);
      })

      .catch((error) => console.log(error));
  }, [days, location]);

  return (
    <Box ml={4}>
      <Grid container spacing={2}>
        {weather && isToday === true && (
          <>
            <Grid item xs={12}>
              <Box mt={2}>
                <Typography variant="h2" className={classes.title}>
                  Today in {weather.location.name}, {""}{" "}
                  {weather.location.country}
                </Typography>
              </Box>
            </Grid>
            <Grid item xs={12}>
              <Box mb={2} mt={2}>
                <Typography variant="h5" className={classes.subTitle}>
                  {format(
                    new Date(weather.location.localtime),
                    LONG_DATE_FORMAT
                  )}
                </Typography>
              </Box>
            </Grid>

            <Grid container spacing={2} direction="row" justify="space-between">
              <Grid item md={1} xs={12}>
                <Box flex="flex-end">
                  <img
                    className={classes.media}
                    src={weather.current.condition.icon}
                    alt="weather"
                  ></img>
                </Box>
              </Grid>
              <Grid item md={2} xs={12}>
                <Typography className={classes.degrees}>
                  {weather.current.temp_c} {SYMBOLS.DEGREES}
                </Typography>
                <Typography className={classes.degrees}>
                  {weather.current.condition.text}
                </Typography>
              </Grid>
              <Grid item md={6} xs={12}>
                <Card
                  className={
                    weather.current.is_day === 1 ? classes.night : classes.root
                  }
                >
                  <CardContent>
                    {dailyForecast.forecastday.map((forecastday, index) => {
                      return (
                        <>
                          <Grid
                            container
                            spacing={2}
                            justify="space-between"
                            justifyContent="center"
                          >
                            <Grid item xs={2}>
                              <Typography className={classes.subTitle}>
                                {forecastday.astro.sunrise}
                                sunset
                              </Typography>
                            </Grid>
                            <Grid item xs={4}>
                              <Typography className={classes.subTitle}>
                                Wind: {weather.current.wind_mph} kph {""}
                                Pressure: {weather.current.pressure_mb} mb
                              </Typography>
                            </Grid>
                          </Grid>

                          <Grid item xs={12}>
                            <Typography className={classes.degrees}>
                              {weather.current.condition.text}
                            </Typography>
                          </Grid>
                        </>
                      );
                    })}
                  </CardContent>
                </Card>
              </Grid>
            </Grid>
          </>
        )}
      </Grid>
    </Box>
  );
};
export default SearchDetails;
