//   scrollbar-width: thin;
//   margin-top: 20px;
//   padding-bottom: 20px;
//   @media ${device.laptop} {
//     order: 4;
//   }
// `;

const WeatherIcon = styled.img`
  display: block;
  height: 50px;
  width: 50px;
  margin: 0 auto;
`;

const useStyles = makeStyles((theme) => ({
  // root: {
  //   width: "100%",
  // },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    fontWeight: theme.typography.fontWeightRegular,
  },
  day: {
    background: "#00BFFF",
    color: "#FFFFFF",
  },
  night: {
    background: "#000000",
    color: "#FFFFFF",
  },
  icon: {
    color: "#FFFFFF",
  },
  root: {
    display: "flex",
    flexWrap: "wrap",
    background: "00bfff",
    "& > *": {
      margin: "4px",
      width: "25px",
      height: "50px",
    },
  },
}));
const HourlyWeather = ({ tabNumber, forecastday }) => {
  const classes = useStyles();

  return (
    <Grid container spacing={2} direction="row">
      {forecastday.hour.map((hourly, index) => {
        return (
          <>
            {/* <Grid
              container
              spacing={2}
              justify="space-between"
              direction="row"
              id={`weather_tab${tabNumber}`}
            > */}
            <Grid item xs={1}>
              <Paper elevation={3} className={classes.root}>
                <Grid item xs={12}>
                  <Typography>
                    {format(new Date(hourly.time), LONG_DATE_TIME_FORMAT)}
                  </Typography>
                </Grid>
                <Grid item xs={12}>
                  <img src={hourly.condition.icon} alt="weather"></img>
                </Grid>
                <Typography>
                  {hourly.temp_c} {SYMBOLS.DEGREES}
                </Typography>
              </Paper>
            </Grid>
            {/* </Grid> */}
          </>
        );
      })}
    </Grid>
  );
};

export default HourlyWeather;
