import React from "react";
import axios from "axios";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Typography from "@material-ui/core/Typography";
import {
  SYMBOLS,
  SHORT_DATE_FORMAT,
  VALID_DATE_FORMAT,
} from "../../Constants/Constants";
import useStyles from "../styles";
import Grid from "@material-ui/core/Grid";
import format from "date-fns/format";
import Tab from "@material-ui/core/Tab";
import Tabs from "@material-ui/core/Tabs";
import { KeyboardDatePicker } from "@material-ui/pickers";
import { Link } from "react-router-dom";

const Next3Days = () => {
  const classes = useStyles();
  const [input, setInput] = useState("");
  const [weather, setWeather] = useState(null);
  const [activeTab, setActiveTab] = useState(0);
  const [forecast, setForecast] = useState(null);
  const [selectedDate, handleDateChange] = useState(new Date());
  const [location, setLocation] = useState("belfast");
  const [days] = useState(3);
  const [isToday, setIsToday] = useState(true);
  const [isForecast, setIsForecast] = useState(false);
  const [dailyForecast, setDailyForecast] = useState({});
  const [isHistory, setIsHistory] = useState(false);
  const [history, setHistory] = useState(null);
  const [historyForecast, setHistoryForecast] = useState({});
  const [date, setDate] = useState(
    format(new Date(selectedDate), VALID_DATE_FORMAT)
  );

  useEffect(() => {
    const getForecast = () => {
      axios
        .get(`${process.env.REACT_APP_FORECAST_API}=${location}&days=${days}`)
        .then((data) => {
          setForecast(data.data);
          setDailyForecast(data.data.forecast);
          setIsForecast(true);
        })
        .catch((error) => console.log(error));
    };
    getForecast();
  }, []);

  return (
    <Grid container spacing="2">
      {forecast && isForecast === true && (
        <Typography variant="h2">
          Next 3 Days for {forecast.location.name}
        </Typography>
        //   {dailyForecast.forecastday.length > 0 ? (
        //     dailyForecast.forecastday.map((forecastday) => {
        //       return (

        //         <Grid xs={12}>
        //           {format(new Date(forecastday.date), SHORT_DATE_FORMAT)}

        //          </Grid>

        //       )
        //     })
        //   ):(null)}
      )}
    </Grid>
  );
};

export default Next3Days;
