import { Typography } from "@material-ui/core";
import React, { useEffect, useState } from "react";
const Favourites = () => {
  const [favourite, setFavourite] = useState(false);
  const [location, setLocation] = useState(false);

  useEffect(() => {
    try {
      const item = localStorage.getItem(`weatherState`);
      console.log(item);
      if (item) {
        const value = JSON.parse(item);

        setLocation(value);
      }
    } catch (error) {
      setFavourite(false);
      console.log(error);
    }
  }, [location]);

  useEffect(() => {
    if (location) {
      localStorage.setItem(location, `weatherState`, JSON.stringify(favourite));
    }
  }, [favourite, location]);
  return <Typography>Your Favourites</Typography>;
};

export default Favourites;
