import { Typography } from "@material-ui/core";
import axios from "axios";
import React, { useEffect, useState, useCallback } from "react";
const Favourites = () => {
  const [favourite, setFavourite] = useState(false);
  const [location, setLocation] = useState("");
  const [weather, setWeather] = useState(null);
  const [today, setIsToday] = useState(false);

  useEffect(() => {
    try {
      const item = localStorage.getItem("weatherState");
      console.log(item);
      if (item) {
        const value = item;
        setLocation(value);
        setFavourite(true);
      }
    } catch (error) {
      setFavourite(false);
      console.log(error);
    }
  }, [location]);
  console.log(location);

  const getToday = useCallback(async () => {
    axios
      .get(`${process.env.REACT_APP_WEATHER_API}=${location}`)
      .then((data) => {
        setWeather(data.data);
        setIsToday(true);
      })
      .catch((error) => console.log(error));
  }, [location]);

  useEffect(() => {
    if (favourite === true) {
      getToday();
    }
  }, []);

  return <Typography>Your Favourites</Typography>;
};

export default Favourites;
