import React, { useState } from "react";
import axios from "axios";
import Grid from "@material-ui/core/Grid";
import { Typography } from "@material-ui/core";
import { Link } from "react-router-dom";
const Search = () => {
  const [input, setInput] = useState("");
  const [searchData, setSearchData] = useState({});
  const [data, setData] = useState(false);
  const [locationName, setLocationName] = useState("");
  //Search event
  const weatherInput = (e) => {
    setInput(e.target.value);
  };

  const searchWeather = () => {
    axios.get(`${process.env.REACT_APP_SEARCH_API}=${input}`).then((data) => {
      console.log(data.data);
      setSearchData(data.data);
      setData(true);
    });
  };

  const name = () => {
    const findName = searchData.map((location) => {
      return location.name.slice("0, 1");
    });
    setLocationName(findName);
  };

  return (
    <Grid container spacing="2">
      <Grid item xs={6}>
        <input onChange={weatherInput} type="text" />
        <button onClick={searchWeather}>Search</button>
      </Grid>
      {data === true &&
        locationName.map((location) => {
          return (
            <>
              <Grid>
                <Typography>{location.name.split(",")}</Typography>
                <Link to={location.url}></Link>
              </Grid>
            </>
          );
        })}
    </Grid>
  );
};

export default Search;
