import React from "react";
// import { useHistory } from "react-router-dom";
import { makeStyles } from "@material-ui/core/styles";
import Accordion from "@material-ui/core/Accordion";
import AccordionSummary from "@material-ui/core/AccordionSummary";
import AccordionDetails from "@material-ui/core/AccordionDetails";
import Typography from "@material-ui/core/Typography";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import Grid from "@material-ui/core/Grid";
import { SYMBOLS } from "../../Constants/Constants";

const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    fontWeight: theme.typography.fontWeightRegular,
  },
  day: {
    background: "#00BFFF",
    color: "#FFFFFF",
  },
  night: {
    background: "#000000",
    color: "#FFFFFF",
  },
  icon: {
    color: "#FFFFFF",
  },
  weather: {
    alignItems: "right",
  },
}));
const HourlyWeather = ({ tabNumber, forecastday }) => {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      {forecastday.hour.map((hourly, index) => {
        return (
          <Grid
            container
            spacing={2}
            justify="space-between"
            id={`weather_tab${tabNumber}`}
          >
            <Accordion
              className={hourly.is_day === 1 ? classes.day : classes.night}
            >
              <AccordionSummary
                expandIcon={<ExpandMoreIcon className={classes.icon} />}
                aria-controls="panel1a-content"
                id="panel1a-header"
              >
                <Typography className={classes.heading}>
                  {hourly.time}

                  <img
                    src={hourly.condition.icon}
                    alt=""
                    className={classes.weather}
                  ></img>
                </Typography>
              </AccordionSummary>
              <AccordionDetails>
                <ul>
                  <Typography>{hourly.condition.text} </Typography>
                  <Typography>
                    Temperature: {hourly.temp_c} {SYMBOLS.DEGREES}{" "}
                  </Typography>
                  <Typography>Feels like: {hourly.feelslike_c} </Typography>
                  <Typography>Wind(mph): {hourly.wind_mph} </Typography>
                  <Typography>Humidity: {hourly.humidity} </Typography>
                  <Typography>
                    Chance of Rain: {hourly.chance_of_rain} %{" "}
                  </Typography>
                </ul>
              </AccordionDetails>
            </Accordion>
          </Grid>
        );
      })}
    </div>
  );
};

export default HourlyWeather;
