import React, { useEffect, useState } from "react";
const Favourites = ({ location, isFavourite }) => {
  const [favourite, setFavourite] = useState(false);
  useEffect(() => {
    if (isFavourite)
      try {
        const item = localStorage.getItem(location, `weatherState`);

        if (item) {
          const value = JSON.parse(item);
          if (typeof value === "boolean") {
            setFavourite(value);
          }
        }
      } catch (error) {
        setFavourite(false);
        console.log(error);
      }
  }, [location, isFavourite]);

  useEffect(() => {
    if (location) {
      localStorage.setItem(location, `weatherState`, JSON.stringify(favourite));
    }
  }, [favourite, location]);

  return <div></div>;
};

export default Favourites;
