import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import { SYMBOLS, TIME_FORMAT } from "../../Constants/Constants";
import format from "date-fns/format";

import GridList from "@material-ui/core/GridList";
import GridListTile from "@material-ui/core/GridListTile";
import GridListTileBar from "@material-ui/core/GridListTileBar";
import useIsMobile from "../../Components/useIsMobile/useIsMobile";

const useStyles = makeStyles((theme) => ({
  day: {
    background: "#00BFFF",
    color: "#FFFFFF",
  },
  night: {
    background: "#000000",
    color: "#FFFFFF",
  },
  icon: {
    color: "#FFFFFF",
  },
  root: {
    flexWrap: "wrap",
    justifyContent: "space-around",
    overflow: "hidden",
  },
  gridList: {
    flexWrap: "nowrap",
    // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
    transform: "translateZ(0)",
  },
  title: {
    color: "#ffffff",
  },
  titleBar: {
    background:
      "linear-gradient(to top, rgba(255,255,255,0) 0%, rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)",
  },
  text: {
    textAlign: "center",
  },
  media: {
    display: "block",
    width: "50%",
    textAlign: "center",
  },
}));
const HourlyWeather = ({ tabNumber, forecastday }) => {
  const classes = useStyles();
  const isMobile = useIsMobile();
  return (
    <Grid container spacing={2} direction="row">
      <Grid item xs={isMobile ? 12 : 12}>
        <div className={classes.root}>
          <GridList
            className={classes.gridList}
            cols={isMobile ? 5 : 14}
            cellHeight={160}
          >
            {forecastday.hour.map((hourly, index) => {
              return (
                <GridListTile key={hourly.condition.icon}>
                  <img
                    src={hourly.condition.icon}
                    alt="weather"
                    className={classes.media}
                  />
                  <GridListTileBar
                    title={format(new Date(hourly.time), TIME_FORMAT)}
                    subtitle={hourly.temp_c + SYMBOLS.DEGREES}
                    classes={{
                      root: classes.titleBar,
                      title: classes.title,
                    }}
                  />
                </GridListTile>
              );
            })}
          </GridList>
        </div>
      </Grid>
    </Grid>
  );
};

export default HourlyWeather;
