import React, { useEffect, useState } from "react";
import axios from "axios";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Typography from "@material-ui/core/Typography";
import { SYMBOLS, LONG_DATE_FORMAT } from "../../Constants/Constants";
import useStyles from "./styles";
import Grid from "@material-ui/core/Grid";
import { useParams } from "react-router-dom";
import Box from "@material-ui/core/Box";
import format from "date-fns/format";

const SearchDetails = () => {
  const classes = useStyles();
  const [weather, setWeather] = useState(null);
  const { location: urlGroupId } = useParams();
  const [location, setLocation] = useState("");
  const [isToday, setIsToday] = useState(true);

  useEffect(() => {
    if (urlGroupId) {
      setLocation(urlGroupId);
    }
  }, [urlGroupId]);

  useEffect(() => {
    if (location !== "") {
      getToday();
    }
  }, [location]);

  const getToday = () => {
    axios
      .get(`${process.env.REACT_APP_WEATHER_API}=${location}`)
      .then((data) => {
        setWeather(data.data);
        console.log(data.data);
        setIsToday(true);
      })
      .catch((error) => console.log(error));
  };

  return (
    <Box ml={4}>
      <Grid container spacing={2}>
        {weather && isToday === true && (
          <>
            <Grid item xs={12}>
              <Box mt={2}>
                <Typography variant="h2" className={classes.title}>
                  Today in {weather.location.name}, {""}{" "}
                  {weather.location.country}
                </Typography>
              </Box>
            </Grid>
            <Grid item xs={12}>
              <Box mb={2} mt={2}>
                <Typography variant="h5" className={classes.subTitle}>
                  {format(
                    new Date(weather.location.localtime),
                    LONG_DATE_FORMAT
                  )}
                </Typography>
              </Box>
            </Grid>

            <Grid container spacing={2} direction="row" justify="space-between">
              <Grid item md={2} xs={12}>
                <img
                  className={classes.media}
                  src={weather.current.condition.icon}
                  alt="weather"
                ></img>
                {/* <Box mt={-5} display="flex">
                  <Typography className={classes.text}></Typography>
                </Box> */}
              </Grid>
              <Grid item xs={12} md={2}>
                <Typography className={classes.degrees}>
                  {weather.current.temp_c} {SYMBOLS.DEGREES}
                  {weather.current.condition.text}
                </Typography>
              </Grid>
              <Grid item xs={12} md={4}>
                <Typography className={classes.degrees}>
                  {weather.current.temp_c} {SYMBOLS.DEGREES}
                </Typography>
              </Grid>
            </Grid>
          </>
        )}
      </Grid>
    </Box>
  );
};
export default SearchDetails;
