import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import Grid from "@material-ui/core/Grid";
import { SYMBOLS, TIME_FORMAT } from "../../Constants/Constants";
import format from "date-fns/format";
import Paper from "@material-ui/core/Paper";

import GridList from "@material-ui/core/GridList";
import GridListTile from "@material-ui/core/GridListTile";
import GridListTileBar from "@material-ui/core/GridListTileBar";

import Box from "@material-ui/core/Box";

const useStyles = makeStyles((theme) => ({
  day: {
    background: "#00BFFF",
    color: "#FFFFFF",
  },
  night: {
    background: "#000000",
    color: "#FFFFFF",
  },
  icon: {
    color: "#FFFFFF",
  },
  root: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "space-around",
    overflow: "hidden",
    backgroundColor: theme.palette.background.paper,
  },
  gridList: {
    flexWrap: "nowrap",
    // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
    transform: "translateZ(0)",
  },
  title: {
    color: theme.palette.primary.light,
  },
  titleBar: {
    background:
      "linear-gradient(to top, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)",
  },
  // root: {
  //   display: "flex",
  //   flexWrap: "wrap",
  //   background: "00bfff",
  //   "& > *": {
  //     margin: "2px",
  //     width: "20px",
  //     height: "40px",
  //   },
  // },
  text: {
    textAlign: "center",
  },
  media: {
    display: "block",
    marginLeft: "auto",
    marginRight: "auto",
    width: "40%",
  },
}));
const HourlyWeather = ({ tabNumber, forecastday }) => {
  const classes = useStyles();

  return (
    <Grid container spacing={2} direction="row" className={classes.root}>
      <GridList className={classes.gridList} cols={2.5}>
        {forecastday.hour.map((hourly, index) => {
          return (
            <>
              <GridListTile>
                <GridListTileBar
                  classes={{
                    root: classes.titleBar,
                    title: classes.title,
                  }}
                />
                {format(new Date(hourly.time), TIME_FORMAT)}
              </GridListTile>
            </>
          );
        })}
      </GridList>
    </Grid>
  );
};

export default HourlyWeather;
