import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Accordion from "@material-ui/core/Accordion";
import AccordionSummary from "@material-ui/core/AccordionSummary";
import AccordionDetails from "@material-ui/core/AccordionDetails";
import Typography from "@material-ui/core/Typography";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import Grid from "@material-ui/core/Grid";
import { SYMBOLS } from "../../Constants/Constants";
import styled from 'styled-components';
import device from '../responsive/Device';

const ForecastWrapper = styled.div`
  flex-shrink: 0;
  flex-basis: 90px;
  padding: 10px;
  margin: 0 5px;
  border-radius: 5px;
  background-color: rgba(255, 255, 255, 0.2);
  &:first-child {
    margin-left: 0;
  }
  &:last-child {
    margin-right: 0;
  }
  @media ${device.tablet} {
    flex-basis: 110px;
  }
  @media ${device.laptop} {
    flex-basis: 125px;
  }
  @media ${device.laptopL} {
    flex-basis: 140px;
  }
`;

const WeatherIcon = styled.img`
  display: block;
  height: 50px;
  width: 50px;
  margin: 0 auto;
`;

const useStyles = makeStyles((theme) => ({
  root: {
    width: "100%",
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    fontWeight: theme.typography.fontWeightRegular,
  },
  day: {
    background: "#00BFFF",
    color: "#FFFFFF",
  },
  night: {
    background: "#000000",
    color: "#FFFFFF",
  },
  icon: {
    color: "#FFFFFF",
  },
}));
const HourlyWeather = ({ tabNumber, forecastday }) => {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      {forecastday.hour.map((hourly, index) => {
        return (
          <Grid
            container
            spacing={2}
            justify="space-between"
            id={`weather_tab${tabNumber}`}
          >
            <ForecastWrapper>
              <Typography>{hourly.time}</Typography>
              <WeatherIcon src={hourly.condition.icon} />
            </ForecastWrapper>

          </Grid>
        );
      })}
    </div>
  );
};

export default HourlyWeather;
