import React, { useEffect, useState } from "react";
import axios from "axios";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Typography from "@material-ui/core/Typography";
import { SYMBOLS } from "../../Constants/Symbols";
import useStyles from "./styles";
import Geocode from "react-geocode";

const Weather = () => {
  Geocode.setApiKey(`${process.env.REACT_APP_GOOGLE_MAPS_API}`);
  const classes = useStyles();
  const [input, setInput] = useState("");
  const [weather, setWeather] = useState(null);
  const [latitude, setLatitude] = useState("");
  const [longitude, setLongitude] = useState("");
  const [location, setLocation] = useState("");

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(function (position) {
      setLatitude(position.coords.latitude);
      setLongitude(position.coords.longitude);
    });
    Geocode.fromLatLng(latitude, longitude).then(
      (response) => {
        const address = response.results[0].formatted_address;
        setLocation(address);
      },
      (error) => {
        console.error(error);
      }
    );
  });

  useEffect(() => {
    axios
      .get(`${process.env.REACT_APP_WEATHER_API}=${location}`)
      .then((data) => {
        setWeather(data.data);
      })
      .catch((error) => console.log(error));
  }, [location]);

  //Search event
  const weatherInput = (e) => {
    setInput(e.target.value);
  };

  const searchWeather = () => {
    axios.get(`${process.env.REACT_APP_WEATHER_API}=${input}`).then((data) => {
      setWeather(data.data);
    });
  };
  return (
    <div>
      <Typography variant="h2">Weather</Typography>
      <div>
        <div>
          <input onChange={weatherInput} type="text" />
          <button onClick={searchWeather}>Search</button>
        </div>
        {weather && (
          <Card className={classes.root}>
            <CardContent>
              <Typography gutterBottom variant="h4" className={classes.text}>
                {weather.location.name}
              </Typography>
              <Typography variant="h5" className={classes.text}>
                {weather.location.country}
              </Typography>
              <Typography className={classes.text}>
                {weather.current.temp_c} {SYMBOLS.DEGREES} {""}
                {weather.current.temp_f} {SYMBOLS.FAHRENHEIT}
              </Typography>
              <Typography className={classes.text}>
                {weather.current.condition.text}
              </Typography>
            </CardContent>
            <CardMedia
              className={classes.media}
              image={weather.current.condition.icon}
            />
          </Card>
        )}
      </div>
    </div>
  );
};

export default Weather;
