import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import Grid from "@material-ui/core/Grid";
import { SYMBOLS, TIME_FORMAT } from "../../Constants/Constants";
import format from "date-fns/format";
import Paper from "@material-ui/core/Paper";

import Box from "@material-ui/core/Box";

const useStyles = makeStyles((theme) => ({
  day: {
    background: "#00BFFF",
    color: "#FFFFFF",
  },
  night: {
    background: "#000000",
    color: "#FFFFFF",
  },
  icon: {
    color: "#FFFFFF",
  },
  root: {
    display: "flex",
    flexWrap: "wrap",
    background: "00bfff",
    "& > *": {
      margin: "2px",
      width: "10px",
      height: "40px",
    },
  },
  text: {
    textAlign: "center",
  },
  media: {
    display: "block",
  },
}));
const HourlyWeather = ({ tabNumber, forecastday }) => {
  const classes = useStyles();

  return (
    <Grid container spacing={2} direction="row">
      {forecastday.hour.map((hourly, index) => {
        return (
          <>
            <Grid item xs={1}>
              <Paper elevation={3} className={classes.root}>
                <Grid item xs={12}>
                  <Typography className={classes.text}>
                    {format(new Date(hourly.time), TIME_FORMAT)}
                  </Typography>
                </Grid>
                <Box justify="center" alignItems="center" mb={2}>
                  <Grid item xs={12}>
                    <img
                      src={hourly.condition.icon}
                      alt="weather"
                      className={classes.media}
                    ></img>
                  </Grid>
                </Box>
                <Grid item xs={12}>
                  <Typography className={classes.text}>
                    {hourly.temp_c} {SYMBOLS.DEGREES}
                  </Typography>
                </Grid>
              </Paper>
            </Grid>
          </>
        );
      })}
    </Grid>
  );
};

export default HourlyWeather;
