import React, { useEffect, useState } from "react";
import axios from "axios";
import Grid from "@material-ui/core/Grid";
import { Typography } from "@material-ui/core";
import Card from "@material-ui/core/Card";
import Link from "@material-ui/core/Link";
import CardContent from "@material-ui/core/CardContent";
import { Link as RouterLink } from "react-router-dom";
import "./search.css";
import SearchIcon from "@material-ui/icons/Search";
const Search = () => {
  const [input, setInput] = useState("");
  const [searchData, setSearchData] = useState({});
  const [data, setData] = useState(false);
  //Search event
  const weatherInput = (e) => {
    setInput(e.target.value);
  };

  useEffect(() => {
    axios.get(`${process.env.REACT_APP_SEARCH_API}=${input}`).then((data) => {
      setSearchData(data.data);
      setData(true);
    });
  }, [input]);

  return (
    <div className="background">
      <Grid container spacing={2}>
        <Grid item xs={6}>
          <div className="wrap">
            <div className="search">
              <input
                className="searchTerm"
                onChange={weatherInput}
                type="text"
                placeholder="Type in a city, country or postcode"
              />
              <button className="searchButton">
                <i className="fa fa-search">
                  <SearchIcon />
                </i>
              </button>
            </div>
          </div>
        </Grid>
        {data === true &&
          searchData.map((location) => {
            return (
              <>
                <Grid container>
                  <Grid item xs={4}>
                    <Card>
                      <CardContent id={location.id}>
                        <Link
                          to={`/search/${location.name.split(",")[0]}`}
                          component={RouterLink}
                          underline="none"
                        >
                          <Typography gutterBottom variant="h4">
                            {location.name.split(",")[0]}
                            {","} {location.country}
                          </Typography>
                        </Link>
                      </CardContent>
                    </Card>
                  </Grid>
                </Grid>
              </>
            );
          })}
      </Grid>
    </div>
  );
};

export default Search;
