import { Typography } from "@material-ui/core";
import React, { useEffect, useState } from "react";
const Favourites = () => {
  const [favourite, setFavourite] = useState(false);
  const [location, setLocation] = useState("");

  useEffect(() => {
    try {
      const item = localStorage.key(0);
      console.log(item);
      if (item) {
        const value = item;
        setLocation(value);
      }
    } catch (error) {
      setFavourite(false);
      console.log(error);
    }
  }, [location]);

  console.log(location);

  //   useEffect(() => {
  //     if (location) {
  //       localStorage.setItem(location, `weatherState`, JSON.stringify(favourite));
  //     }
  //   }, [favourite, location]);
  return <Typography>Your Favourites</Typography>;
};

export default Favourites;
