import React, { useEffect, useState } from "react";
import axios from "axios";
import Grid from "@material-ui/core/Grid";
import { Typography } from "@material-ui/core";
import Card from "@material-ui/core/Card";
import Link from "@material-ui/core/Link";
import CardContent from "@material-ui/core/CardContent";
import { Link as RouterLink } from "react-router-dom";
import ListItemText from "@material-ui/core/ListItemText";
import "./search.css";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import SearchIcon from "@material-ui/icons/Search";
const Search = () => {
  const [input, setInput] = useState("");
  const [searchData, setSearchData] = useState({});
  const [data, setData] = useState(false);
  //Search event
  const weatherInput = (e) => {
    setInput(e.target.value);
  };

  useEffect(() => {
    axios.get(`${process.env.REACT_APP_SEARCH_API}=${input}`).then((data) => {
      setSearchData(data.data);
      setData(true);
    });
  }, [input]);

  return (
    <Grid container spacing={2}>
      <Grid item xs={12}>
        <div className="wrap">
          <div className="search">
            <form autocomplete="off" action="">
              <input
                className="searchTerm"
                onChange={weatherInput}
                type="text"
                placeholder="Type in a city, country or postcode"
              />
              <Grid container spacing={2}>
                <Grid item xs={12}>
                  {data === true &&
                    searchData.map((location, key) => {
                      return (
                        <div>
                          <List dense={true} key={location.id}>
                            <Link
                              to={`/search/${location.name.split(",")[0]}`}
                              component={RouterLink}
                              underline="none"
                            >
                              <ListItem>
                                {location.name.split(",")[0]}
                                {","} {location.country}
                              </ListItem>
                            </Link>
                          </List>
                        </div>
                      );
                    })}

                  {/* <button className="searchButton">
                <i className="fa fa-search">
                  <SearchIcon />
                </i>
              </button> */}
                </Grid>
              </Grid>
            </form>
          </div>
        </div>
      </Grid>
    </Grid>
  );
};

export default Search;
