import React, { useEffect, useState } from "react";
import axios from "axios";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Typography from "@material-ui/core/Typography";
import { SYMBOLS, VALID_DATE_FORMAT } from "../../Constants/Constants";
import useStyles from "../styles";
import Grid from "@material-ui/core/Grid";
import format from "date-fns/format";
import { KeyboardDatePicker } from "@material-ui/pickers";

const History = () => {
  const classes = useStyles();
  const [selectedDate, handleDateChange] = useState(new Date());
  const [location, setLocation] = useState("belfast");
  const [isHistory, setIsHistory] = useState(false);
  const [history, setHistory] = useState(null);
  const [historyForecast, setHistoryForecast] = useState({});
  // const [date, setDate] = useState(
  //   format(new Date(selectedDate), VALID_DATE_FORMAT)
  // );

  const getHistory = () => {
    // const newDate = new Date(selectedDate).toISOString().split("T")[0];
    // setDate(newDate);
    axios
      .get(`${process.env.REACT_APP_HISTORY_API}=${location}&dt=2021-01-01`)
      .then((data) => {
        setHistory(data.data.forecast);
        setHistoryForecast(data.data.forecast);
        console.log(data.data.forecast);
        setIsHistory(true);
      })
      .catch((error) => console.log(error));
  };

  useEffect(() => {
    getHistory();
  });

  return (
    <Grid container spacing="2">
      {history && (
        <Grid container spacing="2">
          {isHistory === true ? (
            historyForecast.forecastday.map((forecastday, index) => {
              return (
                <>
                  {/* <Typography variant="h2">{history.location.name}</Typography> */}
                  <Grid xs={12}>
                    <Grid xs={12}>
                      <KeyboardDatePicker
                        label="Select Date"
                        clearable
                        maxDate={new Date()}
                        format="yyyy/MM/dd"
                        value={selectedDate}
                        onChange={(date) => handleDateChange(date)}
                      />
                    </Grid>
                    {format(new Date(forecastday.date), VALID_DATE_FORMAT)}
                    <Card className={classes.root}>
                      <CardContent>
                        <Typography className={classes.text}>
                          {forecastday.day.avgtemp_c}
                          {SYMBOLS.DEGREES} {""}
                          {forecastday.day.avgtemp_f}
                          {SYMBOLS.FAHRENHEIT}
                        </Typography>
                        <Typography className={classes.text}>
                          Sunrise: {forecastday.astro.sunrise}
                        </Typography>
                        <Typography className={classes.text}>
                          Sunset: {forecastday.astro.sunset}
                        </Typography>

                        <Typography className={classes.text}>
                          {forecastday.day.condition.text}
                        </Typography>
                      </CardContent>
                      <CardMedia
                        className={classes.media}
                        image={forecastday.day.condition.icon}
                      />
                    </Card>
                  </Grid>
                </>
              );
            })
          ) : (
            <Typography>No forecast found</Typography>
          )}
        </Grid>
      )}
    </Grid>
  );
};

export default History;
