import React, { useEffect, useState } from "react";
import axios from "axios";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Typography from "@material-ui/core/Typography";
import { SYMBOLS } from "../../Constants/Symbols";
import useStyles from "./styles";

const Weather = () => {
  const classes = useStyles();
  const [input, setInput] = useState("");
  const [weather, setWeather] = useState(null);
  useEffect(() => {
    axios
      .get(
        "http://api.weatherapi.com/v1/current.json?key=eedf10149dfd418c9ac161655210101&q=Belfast"
      )
      .then((data) => {
        setWeather(data.data);
        console.log(data.data);
      })
      .catch((error) => console.log(error));
  }, []);

  //Search event
  const weatherInput = (e) => {
    setInput(e.target.value);
  };

  const searchWeather = () => {
    axios.get(
      `http://api.weatherapi.com/v1/current.json?key=eedf10149dfd418c9ac161655210101&q=${input}`
    );
  };
  return (
    <div>
      <Typography variant="h2">Weather</Typography>
      <div>
        <div>
          <input onChange={weatherInput} type="text" />
          <button onClick={searchWeather}>Search</button>
        </div>
        {weather && (
          <Card className={classes.root}>
            <CardContent>
              <Typography gutterBottom variant="h4" className={classes.text}>
                {weather.location.name}
              </Typography>
              <Typography variant="h5" className={classes.text}>
                {weather.location.country}
              </Typography>
              <Typography className={classes.text}>
                {weather.current.temp_c} {SYMBOLS.DEGREES} {""}
                {weather.current.temp_f} {SYMBOLS.FAHRENHEIT}
              </Typography>
              <Typography className={classes.text}>
                {weather.current.condition.text}
              </Typography>
            </CardContent>
            <CardMedia
              className={classes.media}
              image={weather.current.condition.icon}
            />
          </Card>
        )}
      </div>
    </div>
  );
};

export default Weather;
