import React, { useEffect, useState } from "react";
import axios from "axios";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Typography from "@material-ui/core/Typography";
import { SYMBOLS } from "../../Constants/Constants";
import useStyles from "../styles";
import Geocode from "react-geocode";
import Grid from "@material-ui/core/Grid";

const Today = () => {
  Geocode.setApiKey(`${process.env.REACT_APP_GOOGLE_MAPS_API}`);
  const classes = useStyles();
  const [weather, setWeather] = useState(null);
  const [latitude, setLatitude] = useState("");
  const [longitude, setLongitude] = useState("");
  const [location, setLocation] = useState("");
  const [isToday, setIsToday] = useState(true);

  const getLocation = () => {
    navigator.geolocation.getCurrentPosition(function (position) {
      setLatitude(position.coords.latitude);
      setLongitude(position.coords.longitude);
    });
    Geocode.fromLatLng(latitude, longitude).then(
      (response) => {
        const address = response.results[0].formatted_address;
        setLocation(address);
      },
      (error) => {
        console.error(error);
      }
    );
  };

  useEffect(() => {
    getLocation();
    const getToday = () => {
      axios
        .get(`${process.env.REACT_APP_WEATHER_API}=${location}`)
        .then((data) => {
          setWeather(data.data);
          setIsToday(true);
        })
        .catch((error) => console.log(error));
    };
    getToday();
  }, [location, latitude, longitude]);

  return (
    <Grid container spacing={2} justify="center">
      {weather && isToday === true && (
        <>
          <Grid item xs={12}>
            <Typography variant="h2">Today</Typography>
          </Grid>
          <Card
            className={
              weather.current.is_day === 1 ? classes.day : classes.root
            }
          >
            <CardContent>
              <Grid item xs={12}>
                <Typography gutterBottom variant="h4" className={classes.title}>
                  {weather.location.name} {""} {weather.location.country}
                </Typography>
              </Grid>
              <Grid
                container
                spacing={2}
                justify="space-between"
                justifyContent="center"
              >
                <Grid item xs={4}>
                  <CardMedia
                    className={classes.media}
                    image={weather.current.condition.icon}
                  />
                </Grid>
                <Grid item xs={4}>
                  <Typography className={classes.degrees}>
                    {weather.current.temp_c} {SYMBOLS.DEGREES}
                  </Typography>
                </Grid>
                <Grid item xs={4}>
                  <Typography className={classes.subTitle}>
                    Wind: {weather.current.wind_mph} kph {""}
                    Pressure: {weather.current.pressure_mb} mb
                  </Typography>
                </Grid>
              </Grid>
              <Grid item xs={12}>
                <Typography className={classes.text}>
                  {weather.current.condition.text}
                </Typography>
              </Grid>
            </CardContent>
          </Card>
        </>
      )}
    </Grid>
  );
};

export default Today;
