import React, { useEffect, useState } from "react";
import axios from "axios";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Typography from "@material-ui/core/Typography";
import { SYMBOLS, SHORT_DATE_FORMAT } from "../../Constants/Constants";
import useStyles from "../styles";
import Grid from "@material-ui/core/Grid";
import format from "date-fns/format";
import Tab from "@material-ui/core/Tab";
import Tabs from "@material-ui/core/Tabs";

const Forecast = () => {
  const classes = useStyles();
  const [forecast, setForecast] = useState(null);
  const [location, setLocation] = useState("belfast");
  const [days] = useState(3);
  const [isForecast, setIsForecast] = useState(false);
  const [dailyForecast, setDailyForecast] = useState({});
  const [activeTab, setActiveTab] = useState(0);

  const getForecast = () => {
    axios
      .get(`${process.env.REACT_APP_FORECAST_API}=${location}&days=${days}`)
      .then((data) => {
        setForecast(data.data);
        setDailyForecast(data.data.forecast);
        setIsForecast(true);
      })
      .catch((error) => console.log(error));
  };

  useEffect(() => {
    getForecast();
  });
  //   const getForecastDay = () => {
  //     const forecast = dailyForecast.forecastday.map((forecastday) => {
  //       return forecastday.date;
  //     });
  //     console.log(forecast);
  //   };

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  return (
    <Grid container spacing="2">
      {forecast && (
        <Grid>
          <Typography variant="h2">
            Forecast for {forecast.location.name}
          </Typography>

          {isForecast === true ? (
            dailyForecast.forecastday.map((forecastday) => {
              return (
                <>
                  {/* {format(new Date(forecastday.date), SHORT_DATE_FORMAT)} */}

                  <Card className={classes.root}>
                    <CardContent>
                      <Typography className={classes.text}>
                        {forecastday.day.avgtemp_c}
                        {SYMBOLS.DEGREES} {""}
                        {forecastday.day.avgtemp_f}
                        {SYMBOLS.FAHRENHEIT}
                      </Typography>
                      <Typography className={classes.text}>
                        Sunrise: {forecastday.astro.sunrise}
                      </Typography>
                      <Typography className={classes.text}>
                        Sunset: {forecastday.astro.sunset}
                      </Typography>

                      <Typography className={classes.text}>
                        {forecastday.day.condition.text}
                      </Typography>
                    </CardContent>
                    <CardMedia
                      className={classes.media}
                      image={forecastday.day.condition.icon}
                    />
                  </Card>
                </>
              );
            })
          ) : (
            <Typography>none found</Typography>
          )}
        </Grid>
      )}
    </Grid>
  );
};

export default Forecast;
