import React, { useEffect, useState, useCallback } from "react";
import axios from "axios";
import Typography from "@material-ui/core/Typography";
import { SHORT_DATE_FORMAT } from "../../Constants/Constants";
import useStyles from "../styles";
import Grid from "@material-ui/core/Grid";
import format from "date-fns/format";
import Tab from "@material-ui/core/Tab";
import Tabs from "@material-ui/core/Tabs";
import WeatherCard from "../../Components/WeatherCard/WeatherCard";
import HourlyWeather from "../../Components/HourlyWeather/HourlyWeather";
import Box from "@material-ui/core/Box";

const Forecast = () => {
  const classes = useStyles();
  const [forecast, setForecast] = useState(null);
  const [location, setLocation] = useState("belfast");
  const [days] = useState(3);
  const [isForecast, setIsForecast] = useState(false);
  const [dailyForecast, setDailyForecast] = useState({});
  const [activeTab, setActiveTab] = useState(0);

  const getForecast = useCallback(async () => {
    axios
      .get(`${process.env.REACT_APP_FORECAST_API}=${location}&days=${days}`)
      .then((data) => {
        setForecast(data.data);
        setDailyForecast(data.data.forecast);
        console.log(data.data.forecast);
        setIsForecast(true);
      })

      .catch((error) => console.log(error));
  }, [days, location]);

  useEffect(() => {
    getForecast();
  }, [getForecast]);

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
  };

  return (
    <>
      <Grid container spacing="2" className={classes.margin}>
        {forecast && (
          <Grid>
            <Typography variant="h2">
              Forecast for {forecast.location.name}
            </Typography>
            {isForecast === true ? (
              <Grid item xs={12}>
                <Tabs
                  value={activeTab}
                  indicatorColor="primary"
                  textColor="primary"
                  onChange={handleTabChange}
                  aria-label="Weather Tab"
                  variant="scrollable"
                >
                  {dailyForecast.forecastday.map((forecastday, index) => {
                    return (
                      <Tab
                        key={`${forecastday.date}_tab_${index}`}
                        label={format(
                          new Date(forecastday.date),
                          SHORT_DATE_FORMAT
                        )}
                      />
                    );
                  })}
                </Tabs>
              </Grid>
            ) : (
              <Typography>No forecast Available</Typography>
            )}
          </Grid>
        )}
      </Grid>

      <Box mt={2} mb={2}>
        <Grid item xs={12} className={classes.margin}>
          {isForecast === true ? (
            dailyForecast.forecastday.map((forecastday, index) => {
              return (
                <React.Fragment
                  key={`display_weather_details_${forecastday.date}`}
                >
                  {activeTab === index && (
                    <Grid container spacin={2}>
                      <Grid item xs={6}>
                        <WeatherCard
                          tabNumber={index}
                          forecastday={forecastday}
                        />
                      </Grid>
                      <Box mt={2} mb={2} display="flex">
                        <Grid item xs={6}>
                          <HourlyWeather
                            tabNumber={index}
                            forecastday={forecastday}
                          />
                        </Grid>
                      </Box>
                    </Grid>
                  )}
                </React.Fragment>
              );
            })
          ) : (
            <Typography>No forecast Available</Typography>
          )}
        </Grid>
      </Box>
    </>
  );
};

export default Forecast;
