import React, { useEffect, useState } from "react";
import axios from "axios";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Typography from "@material-ui/core/Typography";
import { SYMBOLS, SHORT_DATE_FORMAT } from "../../Constants/Constants";
import useStyles from "./styles";
import Geocode from "react-geocode";
import Grid from "@material-ui/core/Grid";

const Weather = () => {
  Geocode.setApiKey(`${process.env.REACT_APP_GOOGLE_MAPS_API}`);
  const classes = useStyles();
  const [input, setInput] = useState("");
  const [weather, setWeather] = useState(null);
  const [forecast, setForecast] = useState(null);
  // const [latitude, setLatitude] = useState("");
  // const [longitude, setLongitude] = useState("");
  // const [location, setLocation] = useState("");
  const [days] = useState(3);
  const [isToday, setIsToday] = useState(true);
  const [isForecast, setIsForecast] = useState(false);
  const [dailyForecast, setDailyForecast] = useState({});

  // useEffect(() => {
  //   navigator.geolocation.getCurrentPosition(function (position) {
  //     setLatitude(position.coords.latitude);
  //     setLongitude(position.coords.longitude);
  //   });
  //   Geocode.fromLatLng(latitude, longitude).then(
  //     (response) => {
  //       const address = response.results[0].formatted_address;
  //       setLocation(address);
  //     },
  //     (error) => {
  //       console.error(error);
  //     }
  //   );
  // });

  useEffect(() => {
    getToday();
  }, []);

  const getToday = () => {
    setIsForecast(false);
    axios
      .get(`${process.env.REACT_APP_WEATHER_API}=Belfast`)
      .then((data) => {
        setWeather(data.data);
        setIsToday(true);
      })
      .catch((error) => console.log(error));
  };

  //Search event
  const weatherInput = (e) => {
    setInput(e.target.value);
  };

  const getForecast = () => {
    setIsToday(false);
    axios
      .get(`${process.env.REACT_APP_FORECAST_API}=Belfast&days=${days}`)
      .then((data) => {
        setForecast(data.data);
        setDailyForecast(data.data.forecast);
        console.log(data.data.forecast);
        setIsForecast(true);
      })
      .catch((error) => console.log(error));
  };

  const searchWeather = () => {
    axios.get(`${process.env.REACT_APP_WEATHER_API}=${input}`).then((data) => {
      setWeather(data.data);
    });
  };

  return (
    <>
      <Grid container spacing="2">
        <Grid item xs={6} className={classes.row}>
          <Typography variant="h2">Weather</Typography>
        </Grid>
        <Grid item xs={6}>
          <input onChange={weatherInput} type="text" />
          <button onClick={searchWeather}>Search</button>
        </Grid>
        <button onClick={getToday}>Today</button>
        <button onClick={getForecast}>Next 3 Days</button>
        <button onClick={getToday}>History</button>
        {weather && isToday === true && (
          <Grid xs={12}>
            <Typography variant="h2">Today</Typography>
            <Card className={classes.root}>
              <CardContent>
                <Typography gutterBottom variant="h4" className={classes.text}>
                  {weather.location.name}
                </Typography>
                <Typography variant="h5" className={classes.text}>
                  {weather.location.country}
                </Typography>
                <Typography className={classes.text}>
                  {weather.current.temp_c} {SYMBOLS.DEGREES} {""}
                  {weather.current.temp_f} {SYMBOLS.FAHRENHEIT}
                </Typography>
                <Typography className={classes.text}>
                  {weather.current.condition.text}
                </Typography>
              </CardContent>
              <CardMedia
                className={classes.media}
                image={weather.current.condition.icon}
              />
            </Card>
          </Grid>
        )}
      </Grid>
      {forecast && isForecast === true && (
        <Grid container spacing="2">
          <Typography variant="h2">
            Next 3 Days for {forecast.location.name}
          </Typography>
          {dailyForecast.forecastday.length > 0 ? (
            dailyForecast.forecastday.map((forecastday) => {
              return (
                <Grid xs={12}>
                  {forecastday.date} {SHORT_DATE_FORMAT}
                  <Card className={classes.root}>
                    <CardContent>
                      <Typography className={classes.text}>
                        {forecastday.day.avgtemp_c} {SYMBOLS.DEGREES} {""}
                        {forecastday.day.avgtemp_f} {SYMBOLS.FAHRENHEIT}
                      </Typography>
                      <Typography className={classes.text}>
                        Sunrise: {forecastday.astro.sunrise}
                      </Typography>
                      <Typography className={classes.text}>
                        Sunset: {forecastday.astro.sunset}
                      </Typography>

                      <Typography className={classes.text}>
                        {forecastday.day.condition.text}
                      </Typography>
                    </CardContent>
                    <CardMedia
                      className={classes.media}
                      image={forecastday.day.condition.icon}
                    />
                  </Card>
                </Grid>
              );
            })
          ) : (
            <Typography>No forecast found</Typography>
          )}
        </Grid>
      )}
    </>
  );
};

export default Weather;
